const parse = (value) => {
  const number = Number(value);
  return Number.isNaN(number) ? Option.none : Option.some(number);
};

// Assume args are '1 2 3':

console.log(args.singleMap(parse));
// Some { value: 1 }

console.log(args.singleMap(parse));
// Some { value: 2 }

console.log(args.singleMap(parse));
// Some { value: 3 }

console.log(args.singleMap(parse));
// None
```

